$(document).ready(function() {
	$(".right .blockB .l h3").last().css("border", "0");
	$(".dealcontent .left .sf .fx").hover(function() {
		$(this).addClass("on");
		$(this).find(".fenxiang").show()
	}, function() {
		$(this).removeClass("on");
		$(this).find(".fenxiang").hide()
	});
	
	$(".qiehuan").hover(function() {
		$(".fk4").show()
	}, function() {
		$(".fk4").hide()
	});
	$(".J_TabBarWrap .tb-tabbar li").each(function(b) {
		var a = $(".J_TabBarWrap").offset().top;
		$(this).click(function() {
			$(this).addClass("selected").siblings().removeClass("selected");
			$(".displayIF").eq(b).show().siblings(".displayIF").hide();
			if ($(".J_TabBarWrap").hasClass("tb-sticky-tabbar")) {
				$(window).scrollTop(a)
			} else {}
		})
	}); if ($(".J_TabBarWrap .tb-tabbar li").index() > 0) {
		$(".displayIF").eq(1).css("display", "none")
	}
	$("#imgbox img").each(function() {
		if ($(this).width() > 0) {
			if ($(this).width() > 680) {
				$(this).width(680)
			}
		}
		$(this).load(function() {
			if ($(this).width() > 680) {
				$(this).width(680)
			}
		})
	})
});
(function(a) {
	tuan800ued.addModule("scrollFollow", {
		OnLoad: function(d, e) {
			if (a(".J_TabBarWrap").length < 1) {
				return false
			}
			var c = a(".J_TabBarWrap"),
				b = c.offset().top;
			a(window).scroll(function() {
				var f = document.body.scrollTop === 0 ? document.documentElement.scrollTop : document.body.scrollTop;
				if (f > b) {
					if (!c.hasClass("tb-sticky-tabbar")) {
						c.addClass("tb-sticky-tabbar")
					}
				} else {
					if (c.hasClass("tb-sticky-tabbar")) {
						c.removeClass("tb-sticky-tabbar")
					}
				}
			})
		}
	})
})(jQuery);